    if (!services) {
      return this.services;
    }

    // Define provider credentials
    // Reformat the ID field
    for (var x in services) {
      if (services.hasOwnProperty(x)) {
        if (typeof(services[x]) !== 'object') {
          services[x] = {
            id: services[x]
          };
        }
      }
    }

